import React from 'react';
import RowCreator from './RowCreator';
import Axios from 'axios';

var COUNTRIES = [{id:"ALG", name:"Algerai", capital:"St. Ann", region:"Asia", subregion:"Southeast"},
{id:"USA", name:"United States", capital:"Washington DC", region:"North America", subregion:"America"},
{id:"BZL", name:"Brazil", capital:"Santigo", region:"South America", subregion:"South America"}];



class DisplayCountries extends React.Component {

    constructor(props){
        super(props);
        this.state = {countries:COUNTRIES,
                      countriesClone:COUNTRIES
        };

        const axios = require('axios');
        axios.get('http://localhost:8080/demo/api/countries')
        .then(res=>{
            console.log(res.data);
        })
    }

    handleOnChange(event){
        var filteredString = event.target.value;
        var filteredCountries = [];

        for(var country of this.state.countries){
            if(country.id.toLowerCase().indexOf(filteredString.toLowerCase())>=0 ||
                country.name.toLowerCase().indexOf(filteredString.toLowerCase())>=0){
                    filteredCountries.push(country);
            }
        }

        this.setState({countriesClone:filteredCountries});
    }


    render(){

        //var countries2 = [{id:"ALG", name:"Algerai", capital:"St. Ann", region:"Asia", subregion:"Southeast"},
        //{id:"USA", name:"United States", capital:"Washington DC", region:"North America", subregion:"America"},
        //{id:"BZL", name:"Brazil", capital:"Santigo", region:"South America", subregion:"South America"}];


        return (<div>

            <h1>Search Countries 2</h1>
            <p><input type="text" placeholder="Search by name" onChange={this.handleOnChange.bind(this)}/></p>
            <div>
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Country</th>
                            <th>Capital</th>
                            <th>Region</th>
                            <th>Sub Region</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.countriesClone.map(country => <RowCreator item={country} key={country.id}/>)}
                    </tbody>
                </table>
            </div>
        </div>)
    }
}

export default DisplayCountries;
