import React from 'react';
import RowCreator from './RowCreator';
import Axios from 'axios';

var TESTCOUNTRIES = [{cioc:"ALG", name:"Algerai", capital:"St. Ann", region:"Asia", subregion:"Southeast"},
{cioc:"USA", name:"United States", capital:"Washington DC", region:"North America", subregion:"America"},
{cioc:"BZL", name:"Brazil", capital:"Santigo", region:"South America", subregion:"South America"}];



class DisplayCountries extends React.Component {

    constructor(props){
        super(props);
        this.state = {countries:[],
                      countriesClone:[]
        };
    }

    componentDidMount() {
        const axios = require('axios');
        const url = 'http://localhost:8080/demo/api/countries';
        axios.get(url).then(res=>{
            console.log(res.data);
            this.setState({countries:res.data,
                           countriesClone:res.data});

        }).catch(error=>{
            console.error('Error', error);
        })
      }

    handleOnChange(event){
        var filteredString = event.target.value;
        var filteredCountries = [];

        for(var country of this.state.countries){
            if(country.cioc.toLowerCase().indexOf(filteredString.toLowerCase())>=0 ||
                country.name.toLowerCase().indexOf(filteredString.toLowerCase())>=0){
                    filteredCountries.push(country);
            }
        }

        this.setState({countriesClone:filteredCountries});
    }


    render(){

        return (<div>
                    <div class="jumbotron text-center">
                        <h1>Search Countries</h1>
                        <p>Demo to filter the list of countries</p>
                    </div>
                    
                    <div class="container">
                        <div class="row">
                            <div class="col-sm-4"/>
                            <div class="col-sm-8"><input type="text" placeholder="Search by name" onChange={this.handleOnChange.bind(this)}/></div>
                        </div>
                        <div class="row">
                            <div class="col-sm-12">
                                <table>
                                    <thead>
                                        <tr>
                                            <th>CIOC</th>
                                            <th>Country</th>
                                            <th>Capital</th>
                                            <th>Region</th>
                                            <th>Sub Region</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {this.state.countriesClone.map(country => <RowCreator item={country} key={country.cioc}/>)}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
        )}
}

export default DisplayCountries;
